package week1.day2;

public class LearnBreak {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=10;i++)
		{	//just want to stop the iteration we use break statement
			if(i==3)
			{
				break;
			}

		
			System.out.println(i);
		}
	}

}
